$(document).ready(function () {
  let staggerLeftRight = document.querySelectorAll('[data-stagger=l-to-r]');
  let staggerUpDown = document.querySelectorAll('[data-stagger=up-down]');
  let tabs = document.querySelectorAll('[data-tab]');
  let tabsContent = document.querySelectorAll('[data-target]');
  let tl = new TimelineMax();

  function stagerAnimate(leftRight, upDown) {
    tl.staggerFromTo(leftRight, 1, {
      x: '-100',
      opacity: 0
    }, {
      x: '0',
      opacity: 1,
      clearProps: 'all'
    }, 0.1).staggerFromTo(upDown, 1, {
      y: '-100',
      opacity: 0
    }, {
      y: '0',
      opacity: 1,
      clearProps: 'all'
    }, 0.1, '<');
  }

  tabs.forEach(item => {
    item.addEventListener('click', event => {
      let data = item.dataset.tab;
      tabsContent.forEach(tabContent => {
        tabContent.classList.remove('active');
      });
      document.querySelector('[data-target = ' + data + ']').classList.add('active');
    });
  });
  stagerAnimate(staggerLeftRight, staggerUpDown);
});